import { useState } from 'react'
import './HomePage.css'
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom'
import validator from 'validator'

export default function StudentResult() {

    return (
        <div className="StudentResult">
            Student Results Management System
            <HomeComponent></HomeComponent>
            <BrowserRouter>
                <Routes>
                    <Route path='/addNewStudent' element={<AddNewStudentComponent/>} />
                    <Route path='/studentList' element={<StudentListComponent/>} />
                    <Route path='/addNewCourse' element={<AddNewCourseComponent/>} />
                    <Route path='/courseList' element={<CourseListComponent/>} />
                    <Route path='/addNewResults' element={<AddNewResultsComponent/>} />
                    <Route path='/resultsList' element={<ResultsListComponent/>} />
                </Routes>
            </BrowserRouter>
            {/*<WelcomeComponent></WelcomeComponent>*/}
        </div>
    )
}

function HomeComponent() {
    const navigate = useNavigate();



    return (
        <div className="Home">
            <div className="HomePage">
            <div> 
                    <label>Add New Students</label>
                    <input type="text" name="addnewstudent" onClick={navigate('/addNewStudent')} />
                </div>
                <div> 
                <a
                    className="AddNewStudent"
                    href="/addNewStudent"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Add New Students
                </a> 
                </div>
                <div> 
                <a
                    className="StudentsList"
                    href="/studentList"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Students List
                </a> 
                </div>
                <div> 
                <a
                    className="AddNewCourses"
                    href="/addNewCourse"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Add New Courses
                </a> 
                </div>
                <div> 
                <a
                    className="CoursesList"
                    href="/courseList"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Courses List
                </a> 
                </div>
                <div> 
                <a
                    className="AddNewResults"
                    href="/addNewResults"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Add New Results
                </a> 
                </div>
                <div> 
                <a
                    className="ResultsList"
                    href="/resultsList"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Results List
                </a> 
                </div>
            </div>
        </div>
    )
}

function AddNewStudentComponent() {
const[username, setUsername] = useState('')
const[password, setPassword] = useState('')
const[familyname, setFamilyname] = useState('')
const[dateofbirth, setDateofbirth] = useState('')
const[email, setEmail] = useState('')
const[showSuccessMessage, setShowSuccessMessage] = useState(false)
const[showErrorMessage, setShowErrorMessage] = useState(false)

const validateEmail = (email) => {

    if (validator.isEmail(email)) {
        setEmail('Valid Email :)')
    } else {
        setEmail('Enter valid Email!')
    }
  }

  const validateDate = (value) => {

    if (validator.isDate(value) && verifyAge(value)) {
        setDateofbirth('Valid Date :)')
    } else {
        setDateofbirth('Enter Valid Date!')
    }
  }

  const verifyAge = (value) => {
    const currentYear = new Date().getFullYear();
    const year = value.split("-")[0];
    const age = currentYear - year;
    console.log(age)
    if (age < 10) setDateofbirth("Age should be more than 10 years");
}


function handleUsernameChange(event) {
    setUsername(event.target.value);
}

function handlePasswordChange(event) {
    console.log(event);
    setPassword(event.target.value);
}

function handleFamilynameChange(event) {
    console.log(event);
    setFamilyname(event.target.value);
}

function handleDateofbirthChange(event) {
    console.log(event);
    setDateofbirth(event.target.value);
}

function handleEmailChange(event) {
    console.log(event);
    setEmail(event.target.value);
}

function handleSubmit() {
    console.log(username);
    console.log(password);
    if(username!=='' && familyname!=='' && dateofbirth!=='' && email!=='' ){
        validateEmail(email)
        validateDate(dateofbirth)
        setShowSuccessMessage(true);
        setShowErrorMessage(false);
    } else {
        setShowSuccessMessage(false);
        setShowErrorMessage(true);
    }
}

function SuccessMessageComponent() {
    if(showSuccessMessage)
        return <div className="successMessage">Authentication success</div>
    return null
}

function ErrorMessageComponent() {
    if(showErrorMessage)
        return <div className="errorMessage">Authentication failed</div>
    return null
}

    return (
        <div className="AddNewStudent">
            <div className="AddNewStudentForm">
                {showSuccessMessage && <div className="successMessage">Authentication success</div>}
                {showErrorMessage && <div className="errorMessage">Authentication failed, Please check your Credentials</div>}
                <div> 
                    <label>FirstName:</label>
                    <input type="text" name="username" value={username} onChange={handleUsernameChange} />
                </div>
                <div> 
                    <label>FamilyName:</label>
                    <input type="text" name="familyname" value={familyname} onChange={handleFamilynameChange} />
                </div>
                <div> 
                    <label>DateOfBirth(yyyy-mm-dd):</label>
                    <input type="text" name="dateofbirth" value={dateofbirth} onChange={handleDateofbirthChange} />
                </div>
                <div> 
                    <label>Email Address:</label>
                    <input type="text" name="email" value={email} onChange={handleEmailChange} />
                </div>
                <div> 
                    <label>Password:</label>
                    <input type="password" name="password" value={password} onChange={handlePasswordChange}  />
                </div>
                <div> 
                    <button type="button" name="submit" onClick={handleSubmit}>submit</button>
                </div>
            </div>
        </div>
    )
}


function StudentListComponent() {
    const data ={

        "student" :[
          {
            "name":"John",
            "last_name":"Doe",
            "age":"25",
            "Occupation":"driver",
          },
          {
            "name":"Jack",
            "last_name":"Brown",
            "age":"24",
            "Occupation":"it"
          },
          {
            "name":"Oliver",
            "last_name":"Black",

            "age":"30",
            "Occupation":"cto"
          },
        ],

        "format":{"last_name":"Last Name"}
      }

    return (
    <div className="StudentsList">
        Student Lists

        <tbody>
          <tr>
            <th>Name</th>
            <th>Family Name</th>
            <th>DOB</th>
            <th>Email</th>
            <th>Delete</th>
          </tr>
          {data.student.map((item, i) => (
            <tr key={i}>
              <td>{item.name}</td>
              <td>{item.last_name}</td>
              <td>{item.age}</td>
              <td>{item.Occupation}</td>
            </tr>
          ))}
    </tbody>

    </div>
    )
}

function AddNewCourseComponent() {
    const[coursename, setCoursename] = useState('')
    const[showSuccessMessage, setShowSuccessMessage] = useState(false)
    const[showErrorMessage, setShowErrorMessage] = useState(false)

    function handleCoursenameChange(event) {
        setCoursename(event.target.value);
    }

    function handleSubmit() {
        if(coursename!==''){
            setShowSuccessMessage(true);
            setShowErrorMessage(false);
        } else {
            setShowSuccessMessage(false);
            setShowErrorMessage(true);
        }
    }

    return (
        <div className="AddNewCourses">
            <div className="AddNewCourseForm">
                {showSuccessMessage && <div className="successMessage">Course added successfully</div>}
                {showErrorMessage && <div className="errorMessage">Course addition failed, Please try again</div>}
                <div> 
                    <label>CourseName:</label>
                    <input type="text" name="coursename" value={coursename} onChange={handleCoursenameChange} />
                </div>
                <div> 
                    <button type="button" name="submit" onClick={handleSubmit}>submit</button>
                </div>
            </div>
        </div>
    )
}

function CourseListComponent() {
    const data ={

        "course" :[
          {
            "coursename":"John"
          },
          {
            "coursename":"Jack"
          },
          {
            "coursename":"Oliver"
          },
        ]
      }

    return (
    <div className="CoursesList">
        Course Lists

        <tbody>
          <tr>
            <th>Course Name</th>
            <th>Delete</th>
          </tr>
          {data.course.map((item, i) => (
            <tr key={i}>
              <td>{item.coursename}</td>
            </tr>
          ))}
    </tbody>

    </div>
    )
}

function AddNewResultsComponent() {
    const[coursename, setCoursename] = useState('')
    const[studentname, setStudentname] = useState('')
    const[score, setScore] = useState('')
    const[showSuccessMessage, setShowSuccessMessage] = useState(false)
    const[showErrorMessage, setShowErrorMessage] = useState(false)

    function handleCoursenameChange(event) {
        setCoursename(event.target.value);
    }

    function handleStudentnameChange(event) {
        setStudentname(event.target.value);
    }

    function handleScoreChange(event) {
        setScore(event.target.value);
    }

    function handleSubmit() {
        if(coursename!=='' && studentname!=='' && score!==''){
            setShowSuccessMessage(true);
            setShowErrorMessage(false);
        } else {
            setShowSuccessMessage(false);
            setShowErrorMessage(true);
        }
    }

        return (
            <div className="AddNewResults">
                <div className="AddNewResultsForm">
                    {showSuccessMessage && <div className="successMessage">Results Added successfully</div>}
                    {showErrorMessage && <div className="errorMessage">Result addition failed, Try again</div>}
                    <div> 
                        <label>CourseName:</label>
                        <input type="text" name="coursename" value={coursename} onChange={handleCoursenameChange} />
                    </div>
                    <div> 
                        <label>StudentName:</label>
                        <input type="text" name="studentname" value={studentname} onChange={handleStudentnameChange} />
                    </div>
                    <div> 
                        <label>Score:</label>
                        <input type="text" name="score" value={score} onChange={handleScoreChange} />
                    </div>
                    <div> 
                        <button type="button" name="submit" onClick={handleSubmit}>submit</button>
                    </div>
                </div>
            </div>
        )
}

function ResultsListComponent() {
    const data ={

        "results" :[
          {
            "coursename":"John",
            "studentname":"Doe",
            "score":"25"
          },
          {
            "coursename":"Jack",
            "studentname":"Brown",
            "score":"24"
          },
          {
            "coursename":"Oliver",
            "studentname":"Black",
            "score":"30"
          },
        ]
      }

    return (
    <div className="ResultsList">
        Student Lists

        <tbody>
          <tr>
            <th>Course</th>
            <th>Student</th>
            <th>Score</th>
          </tr>
          {data.results.map((item, i) => (
            <tr key={i}>
              <td>{item.coursename}</td>
              <td>{item.studentname}</td>
              <td>{item.score}</td>
            </tr>
          ))}
    </tbody>

    </div>
    )
}
