import { useState } from 'react'
import './HomePage.css'
import { Routes, Route, useNavigate, Link } from 'react-router-dom'
import validator from 'validator'
import FooterComponent from './FooterComponent'
import HeaderComponent from './HeaderComponent'
import ResultsListComponent from './ResultsListComponent'
import AddNewResultsComponent from './AddNewResultsComponent'
import CourseListComponent from './CourseListComponent'
import AddNewCourseComponent from './AddNewCourseComponent'
import StudentListComponent from './StudentListComponent'

export default function StudentResult() {

    return (
        <div className="StudentResult">
            <h1>Student Results Management System</h1>
            <HeaderComponent/>
            <HomeComponent></HomeComponent>
                <Routes>
                    <Route path='/addNewStudent' element={<AddNewStudentComponent/>} />
                    <Route path='/studentList' element={<StudentListComponent/>} />
                    <Route path='/addNewCourse' element={<AddNewCourseComponent/>} />
                    <Route path='/courseList' element={<CourseListComponent/>} />
                    <Route path='/addNewResults' element={<AddNewResultsComponent/>} />
                    <Route path='/resultsList' element={<ResultsListComponent/>} />
                </Routes>
                <FooterComponent/>
            {/*<WelcomeComponent></WelcomeComponent>*/}
        </div>
    )
}

function HomeComponent() {

    return (
        <div className="Home">
            <div className="HomePage">

                <div> 
                    <Link to="/addNewStudent">Add New Students</Link>
                </div>
                <div> 
                    <Link to="/studentList">Students List</Link>
                </div>
                <div> 
                    <Link to="/addNewCourse">Add New Courses</Link>
                </div>
                <div> 
                    <Link to="/courseList">Courses List</Link>
                </div>
                <div> 
                    <Link to="/addNewResults">Add New Results</Link>
                </div>
                <div> 
                    <Link to="/resultsList">Results List</Link>
                </div>
            </div>
        </div>
    )
}

function AddNewStudentComponent() {
const[username, setUsername] = useState('')
const[password, setPassword] = useState('')
const[familyname, setFamilyname] = useState('')
const[dateofbirth, setDateofbirth] = useState('')
const[email, setEmail] = useState('')
const[showSuccessMessage, setShowSuccessMessage] = useState(false)
const[showErrorMessage, setShowErrorMessage] = useState(false)

const validateEmail = (email) => {

    if (validator.isEmail(email)) {
        setEmail('Valid Email :)')
    } else {
        setEmail('Enter valid Email!')
    }
  }

  const validateDate = (value) => {

    if (validator.isDate(value) && verifyAge(value)) {
        setDateofbirth('Valid Date :)')
    } else {
        setDateofbirth('Enter Valid Date!')
    }
  }

  const verifyAge = (value) => {
    const currentYear = new Date().getFullYear();
    const year = value.split("-")[0];
    const age = currentYear - year;
    console.log(age)
    if (age < 10) setDateofbirth("Age should be more than 10 years");
}


function handleUsernameChange(event) {
    setUsername(event.target.value);
}

function handlePasswordChange(event) {
    console.log(event);
    setPassword(event.target.value);
}

function handleFamilynameChange(event) {
    console.log(event);
    setFamilyname(event.target.value);
}

function handleDateofbirthChange(event) {
    console.log(event);
    setDateofbirth(event.target.value);
}

function handleEmailChange(event) {
    console.log(event);
    setEmail(event.target.value);
}

function handleSubmit() {
    console.log(username);
    console.log(password);
    if(username!=='' && familyname!=='' && dateofbirth!=='' && email!=='' ){
        validateEmail(email)
        validateDate(dateofbirth)
        setShowSuccessMessage(true);
        setShowErrorMessage(false);
    } else {
        setShowSuccessMessage(false);
        setShowErrorMessage(true);
    }
}

function SuccessMessageComponent() {
    if(showSuccessMessage)
        return <div className="successMessage">Authentication success</div>
    return null
}

function ErrorMessageComponent() {
    if(showErrorMessage)
        return <div className="errorMessage">Authentication failed</div>
    return null
}

    return (
        <div className="AddNewStudent">
            <div className="AddNewStudentForm">
                <h1>Add New Students</h1>
                {showSuccessMessage && <div className="successMessage">Authentication success</div>}
                {showErrorMessage && <div className="errorMessage">Authentication failed, Please check your Credentials</div>}
                <div> 
                    <label>FirstName:</label>
                    <input type="text" name="username" value={username} onChange={handleUsernameChange} />
                </div>
                <div> 
                    <label>FamilyName:</label>
                    <input type="text" name="familyname" value={familyname} onChange={handleFamilynameChange} />
                </div>
                <div> 
                    <label>DateOfBirth(yyyy-mm-dd):</label>
                    <input type="text" name="dateofbirth" value={dateofbirth} onChange={handleDateofbirthChange} />
                </div>
                <div> 
                    <label>Email Address:</label>
                    <input type="text" name="email" value={email} onChange={handleEmailChange} />
                </div>
                <div> 
                    <label>Password:</label>
                    <input type="password" name="password" value={password} onChange={handlePasswordChange}  />
                </div>
                <div> 
                    <button type="button" name="submit" onClick={handleSubmit}>submit</button>
                </div>
            </div>
        </div>
    )
}
