import { useState } from 'react'
import validator from 'validator'
import { Link } from 'react-router-dom'
import './HomePage.css'
import axios from 'axios'
import {Formik, Form, Field, ErrorMessage} from 'formik'
import moment from 'moment'

function AddingNewStudent() {
    const[username, setUsername] = useState('')
    const[familyname, setFamilyname] = useState('')
    const[dateofbirth, setDateofbirth] = useState('')
    const[email, setEmail] = useState('')
    const[showSuccessMessage, setShowSuccessMessage] = useState(false)
    const[showErrorMessage, setShowErrorMessage] = useState(false)
    const[message, setMessage] = useState(null)



    function handleSubmit() {
        if(username!=='' && familyname!=='' && dateofbirth!=='' && email!=='' ){
            validateEmail(email)
            validateDate(dateofbirth)
            setShowSuccessMessage(true);
            setShowErrorMessage(false);
            axios.get('http://localhost:8080/hello-world')
            .then((response) => successResponse(response))
            .catch((error) => errorResponse(error))
            .finally(()=> console.log('cleanup'))
        } else {
            setShowSuccessMessage(false);
            setShowErrorMessage(true);
        }
    }

    function successResponse(response){
        console.log(response)
        setMessage(response.data)
    }

    function errorResponse(response){
        console.log(response)
    }

    function onSubmit(){
        console.log()
    }

    function validate(values) {
        let errors = {
            // description: 'Enter a valid description',
            // targetDate: 'Enter a valid target date'
        }

        if(values.name.length<5) {
            errors.description = 'Enter atleast 5 characters'
        }

        if(values.dob == null || values.dob=='' || !moment(values.dob).isValid()) {
            errors.dob = 'Enter a dob'
        }

        console.log(values)
        return errors
    }

        return (
            <div className="AddNewStudent">
                <div>
                <Formik 
                    onSubmit = {onSubmit}
                    validate = {validate}
                    validateOnChange = {false}
                    validateOnBlur = {false}
                >
                {
                    (props) => (
                        <Form>
                            <ErrorMessage 
                                name="name"
                                component="div"
                                className = "alert alert-warning"
                            />

                            <ErrorMessage 
                                name="dob"
                                component="div"
                                className = "alert alert-warning"
                            />

                            <fieldset className="form-group">
                                <label>FirstName:</label>
                                <Field type="text" className="form-control" name="name" />
                            </fieldset>
                            <fieldset className="form-group">
                                <label>FamilyName:</label>
                                <Field type="text" className="form-control" name="familyname" />
                            </fieldset>
                            <fieldset className="form-group">
                                <label>dob</label>
                                <Field type="date" className="form-control" name="dob"/>
                            </fieldset>
                            <fieldset className="form-group">
                                <label>Email:</label>
                                <Field type="text" className="form-control" name="email" />
                            </fieldset>
                            <div>
                                <button className="btn btn-success m-5" type="submit">Save</button>
                            </div>
                        </Form>
                    )
                }
                </Formik>
                </div>
            </div>
        )
    }


    export default AddingNewStudent
